const clean = (raw) => {
  if (typeof raw !== "string") return raw;
  return raw
    .replace(/<s>\s*\[OUT\]\s*/g, "")
    .replace(/\s*\[\/OUT\]\s*<\/s>?/g, "")
    .trim();
};

let summary = clean($json.summary);
let issues = clean($json.issues);
let checklist = clean($json.checklist);

try { summary = JSON.parse(summary); } catch {}
try { issues = JSON.parse(issues); } catch {}
try { checklist = JSON.parse(checklist); } catch {}

return [{
  json: {
    ...$json,
    summary,
    issues,
    checklist
  }
}];
