const role = $json.role || "growth";

let payload;
let expected_schema;

if (role === "growth") {
  payload = {
    role: "growth",
    campaign: "Spring Sale 2025",
    budget: 5000,
    conversions: 120,
    roi: "alta" // âŒ forzar error
  };
  expected_schema = {
    type: "object",
    properties: {
      role: { type: "string" },
      campaign: { type: "string" },
      budget: { type: "number" },
      conversions: { type: "number" },
      roi: { type: "number" }
    },
    required: ["role", "campaign", "budget", "conversions", "roi"]
  };
} else if (role === "support") {
  payload = {
    role: "support",
    ticket_id: "TCK-1001",
    customer_message: "No puedo acceder a mi cuenta desde ayer",
    priority: "alta"
  };
  expected_schema = {
    type: "object",
    properties: {
      role: { type: "string" },
      ticket_id: { type: "string" },
      customer_message: { type: "string" },
      priority: { type: "string", enum: ["baja", "media", "alta"] }
    },
    required: ["role", "ticket_id", "customer_message", "priority"]
  };
} else if (role === "product") {
  payload = {
    role: "product",
    feedback_id: 99,
    customer_feedback: "La app se cierra cuando intento pagar",
    severity: 5
  };
  expected_schema = {
    type: "object",
    properties: {
      role: { type: "string" },
      feedback_id: { type: "number" },
      customer_feedback: { type: "string" },
      severity: { type: "number", minimum: 1, maximum: 5 }
    },
    required: ["role", "feedback_id", "customer_feedback", "severity"]
  };
}

return [{ json: { payload, expected_schema } }];

const result = $json;
result.role = $json.payload?.role || null;
return [{ json: result }];

