const clean = (raw) => {
  if (typeof raw !== "string") return raw;
  return raw
    .replace(/<s>/g, "")
    .replace(/```json|```/g, "")
    .trim();
};

// extraer el contenido generado por la IA
let content = $json.choices?.[0]?.message?.content ?? "";
content = clean(content);

let parsed = {};
if (content && (content.startsWith("{") || content.startsWith("["))) {
  try {
    parsed = JSON.parse(content);
  } catch (e) {
    parsed = {};
  }
}

// salida normalizada para Slack
return [{
  json: {
    role: parsed.role || "â€”",
    category: parsed.category || "â€”",
    confidence: parsed.confidence ?? "â€”",
    status: parsed.status || "â€”",
